<?phpclass Connection{  private $host = 'localhost';  private $username = 'root';  private $password = '';  private $dbName = 'touristHub';  public function Connect()  {      $conn = new mysqli($this->host,$this->username,$this->password,$this->dbName);      if ($conn->connect_error){          echo 'Connection Problem'. $conn->connect_error;      }      return $conn;  }}