<?phpclass Connection{  private string $host = 'localhost';  private string $username = 'root';  private string $password = '';  private string $dbName = 'touristHub2';  public function Connect()  {      $conn = new mysqli($this->host,$this->username,$this->password,$this->dbName);      if ($conn->connect_error){          echo 'Connection Problem'. $conn->connect_error;      }      return $conn;  }}