<?phpnamespace controllers;use models\auth;require '../models/auth.php';class authController{    public function storeUser():void    {         $Fields = [             'FirstName' => 'first name is required',             'LastName' => 'last name is required',             'Username' => 'username is required',             'password' => 'password is required',             'email' => 'email is required',             'Address' => 'address is required',             'phoneNumber' => 'phone number is required',              'contactPerson' => 'contact person is required'         ];         $data = [];         $error = [];        foreach ($Fields as $field => $message){            if (!isset($_POST[$field]) || trim($_POST[$field]) === ''){                $error[$field] = $message;            }else{                $data[$field] = htmlspecialchars($_POST[$field]);            }        }        if (!empty($error)){            $data['success'] = false;            $data['errors'] = $error;            echo json_encode($data);            return;        }        $storeUser = new auth();        $passwordHashed = password_hash($data['password'],PASSWORD_BCRYPT);        $storeUser->registerUser(            $data['FirstName'],            $data['LastName'],            $data['Username'],            $passwordHashed,            $data['email'],            $data['Address'],            $data['phoneNumber'],            $data['contactPerson']        );    }    public function login():void    {        $Fields = [            'Password' => 'password is required',            'username' => 'username is required'        ];        $data = [];        $error = [];        foreach ($Fields as $field => $message){            if (!isset($_POST[$field]) || trim($_POST[$field]) === ''){                $error[$field] = $message;            }else{                $data[$field] = htmlspecialchars($_POST[$field]);            }        }        if (!empty($error)){            $data['success'] = false;            $data['errors'] = $error;            echo json_encode($data);            return;        }        $loginUser = new auth();        $loginUser->loginUser($data['username'],$data['Password']);    }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])){    $userController =  new authController();    match ($_POST['action']){        'store' => $userController->storeUser(),        'login' => $userController->login()    };}