<?phpnamespace controllers;use models\uploadingImage;include_once '../models/uploadingImage.php';class uploadImageController{    public function store(): void    {        $image = $_FILES['image'] ?? null;        $description = $_POST['Description'] ?? '';        $touristSpotListId = $_POST['touristSpotListId'] ?? '';        $EventListId = $_POST['EventListId'] ?? '';        // Validate required inputs        $errors = [];        if (empty($touristSpotListId) && empty($EventListId)) {            $errors[] = 'Please select tourist spot or event.';        }        if (empty($description)) {            $errors[] = 'Description is required.';        }        if (empty($image) || empty($image['name'])) {            $errors[] = 'Image is required.';        }        // Return errors if validation fails        if (!empty($errors)) {            echo json_encode(['success' => false, 'message' => implode(' ', $errors)]);            return;        }        // Handle image upload        $fileDir = '../../assets/imageUploaded/';        $fileInfo = pathinfo($image['name']);        $fileExtension = strtolower($fileInfo['extension']);        $fileName = uniqid('UploadedImage_') . '.' . $fileExtension;        $uploadPath = $fileDir . $fileName;        if (!move_uploaded_file($image['tmp_name'], $uploadPath)) {            echo json_encode(['success' => false, 'message' => 'Image upload failed.']);            return;        }        $imageName = $fileName;        // Store the image and description for the selected tourist spot        if (!empty($touristSpotListId)) {            (new uploadingImage())->store($imageName, $description, $touristSpotListId);        }        // Store the image and description for the selected Events        if (!empty($EventListId)) {            (new uploadingImage())->store($imageName, $description, $EventListId);        }    }    public static function showAll():void    {        $images = new uploadingImage();        $data = $images->showAll();        $tableTr = '';        if ($data){            foreach ($data as $row){                $tableTr .= '                  <tr>                       <th>'.$row['id'].'</th>                       <td>                        <img style="height: 50px; width: 50px; object-fit: cover;" class="img-fluid" src="../../assets/imageUploaded/'.$row['image'].'" alt="image">                       </td>                       <td>'.$row['event_or_touristId'].'</td>                       <td>                        <button class="btn btn-primary">Edit</button>                        <button class="btn btn-danger">Delete</button>                      </td>                 </tr>               ';            }            echo $tableTr;        }    }    public static function images():void    {        $image = new uploadingImage();        $sum = $image->countedImages();        echo "<h4>{$sum['countedImages']}</h4>";    }}if ($_SERVER['REQUEST_METHOD'] === 'POST' && isset($_POST['action'])){    match ($_POST['action']){        'store' => (new uploadImageController())->store(),        default => http_response_code(400)    };}