<?phpnamespace models;require_once '../../config/Connection.php';class events extends \Connection{    public function store(string $EventName, string $description,string $image,string $date ,string $location):void // store the tourist spot information    {        $conn = $this->Connect();        $query = "INSERT INTO event(evnetName, description, image, date,location)values (?,?,?,?,?)";        $stmt = $conn->prepare($query);        $stmt->bind_param('sssss',$EventName,$description,$image,$date ,$location);        if ($stmt->execute()){            echo json_encode(['success' => true, 'message' => 'Successfully Created New Event']);        }else{            echo json_encode(['success' => false, 'message' => 'Error'.$stmt->error]);        }        $stmt->close();        $this->Connect()->close();    }    public function showAll():array// show all the tourist Spot list    {        $conn = $this->Connect();        $result = $conn->query("SELECT * FROM event");        $data = [];        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $data[] = $row;            }            return $data;        }        return [];    }    public function showBaseOnId($id):array    {        $conn = $this->Connect();        $query = "SELECT * FROM event WHERE id = ?";        $stmt = $conn->prepare($query);        $stmt->bind_param('i',$id);        $stmt->execute();        $result = $stmt->get_result();        $data = [];        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $data[] = $row;            }            return $data;        }        return [];    }    public function update(string $eventName, string $description, int $id):void    {        $conn = $this->Connect();        $query = "UPDATE event SET evnetName = ? , description = ? WHERE id = ?";        $stmt = $conn->prepare($query);        $stmt->bind_param('ssi',$eventName,$description,$id);        if ($stmt->execute()){            echo json_encode(['success' =>  true, 'message' => 'Successfully Update'. ' '.$eventName]);        }else{            echo json_encode(['success' => false, 'message' => 'Error',$stmt->error]);        }    }    public function Delete(int $id):void    {        $conn = $this->Connect();        $query = "DELETE FROM event WHERE id = ?";        $stmt = $conn->prepare($query);        $stmt->bind_param('i',$id);        if ($stmt->execute()){            echo json_encode(['success' =>  true, 'message' => 'Successfully Deleted']);        }else{            echo json_encode(['success' => false, 'message' => 'Error',$stmt->error]);        }    }}