<?phpnamespace models;require_once '../../config/Connection.php';class events extends \Connection{    public function store(string $EventName, string $description,string $image,string $date ,string $location):void // store the tourist spot information    {        $conn = $this->Connect();        $query = "INSERT INTO event(evnetName, description, image, date,location)values (?,?,?,?,?)";        $stmt = $conn->prepare($query);        $stmt->bind_param('sssss',$EventName,$description,$image,$date ,$location);        if ($stmt->execute()){            echo json_encode(['success' => true, 'message' => 'Successfully Created New Event']);        }else{            echo json_encode(['success' => false, 'message' => 'Error'.$stmt->error]);        }        $stmt->close();        $this->Connect()->close();    }    public function showAll():array// show all the tourist Spot list    {        $conn = $this->Connect();        $result = $conn->query("SELECT * FROM event");        $data = [];        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $data[] = $row;            }            return $data;        }        return [];    }}