<?phpnamespace models;require_once '../../config/Connection.php';class municipality extends \Connection{    public function store(string $municipalityName, string $image, string $description):void    {        $conn = $this->Connect();        $query = "INSERT INTO municipality(municipalityName, image, description)values (?,?,?)";        $stmt = $conn->prepare($query);        $stmt->bind_param('sss',$municipalityName,$image ,$description);        if ($stmt->execute()){            echo json_encode(['success' => true, 'message' => 'Successfully Created New Municipality']);        }else{            echo json_encode(['success' => false, 'message' => 'Error'.$stmt->error]);        }        $stmt->close();        $this->Connect()->close();    }    public function showIdAndTitle():array    {        $conn = $this->Connect();        $result = $conn->query("SELECT municipalityName , id FROM municipality");        $data = [];        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $data[] = $row;            }            return $data;        }        return [];    }    public function showAll():array// show all the tourist Spot list    {        $conn = $this->Connect();        $result = $conn->query("SELECT * FROM municipality");        $data = [];        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $data[] = $row;            }            return $data;        }        return [];    }}