<?phpnamespace models;require_once '../../config/Connection.php';class uploadingImage extends \Connection{    public function store(string $image,string $description, int $id):void // store the tourist spot information    {        $conn = $this->Connect();        $query = "INSERT INTO images(image, description, event_or_touristId)values(?,?,?)";        $stmt = $conn->prepare($query);        $stmt->bind_param('ssi',$image,$description,$id);        if ($stmt->execute()){            echo json_encode(['success' => true, 'message' => 'Successfully Added New Image']);        }else{            echo json_encode(['success' => false, 'message' => 'Error'.$stmt->error]);        }        $stmt->close();        $this->Connect()->close();    }    public function showAll():array    {        $conn = $this->Connect();        $result = $conn->query("SELECT * FROM images");        $data = [];        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $data[] = $row;            }            return $data;        }        return [];    }    public function showBaseOnTouristId_orEventId($id):array    {        $conn = $this->Connect();        $query = "SELECT * FROM images WHERE event_or_touristId = ?";        $stmt = $conn->prepare($query);        $stmt->bind_param('i',$id);        $stmt->execute();        $result = $stmt->get_result();        $data = [];        if ($result->num_rows > 0){            while ($row = $result->fetch_assoc()){                $data[] = $row;            }            return $data;        }        return [];    }}